SELECT CS.CHALLENGE_SUBMISSION_ID AS SUBMISSION_ID ,CS.SUBMISSION_IMAGE_ROUTE AS SUBMISSION_IMAGE ,CS.SUBMISSION_DATE ,(SELECT COUNT(*) FROM LIKES JOIN CHALLENGE_SUBMISSION CS ON LIKES.CHALLENGE_SUBMISSION_ID = CS.CHALLENGE_SUBMISSION_ID WHERE LIKES.CHALLENGE_SUBMISSION_ID = SUBMISSION_ID) AS LIKE_COUNT ,(SELECT COUNT(*) FROM COMMENT CM JOIN CHALLENGE_SUBMISSION CS ON CM.CHALLENGE_SUBMISSION_ID = CS.CHALLENGE_SUBMISSION_ID WHERE CM.CHALLENGE_SUBMISSION_ID = SUBMISSION_ID) AS COMMENT_COUNT FROM CHALLENGE_SUBMISSION CS JOIN USER_CHALLENGE UC ON CS.USER_CHALLENGE_ID = UC.USER_CHALLENGE_ID
JOIN SUBMISSION_HASH_TAG SHT ON CS.CHALLENGE_SUBMISSION_ID = SHT.CHALLENGE_SUBMISSION_ID WHERE SHT.HASH_TAG_ID IN
#{tag}
ORDER BY SUBMISSION_DATE DESC LIMIT #{startPage} , #{endPage}

SELECT count(*) as own 
 FROM CHALLENGE_SUBMISSION CS 
  JOIN USER_CHALLENGE UC 
   ON CS.USER_CHALLENGE_ID = UC.USER_CHALLENGE_ID
	  WHERE YEAR(STR_TO_DATE(CS.submission_date, '%Y-%m-%d')) = #{year} 
	  	AND MONTH(STR_TO_DATE(CS.submission_date, '%Y-%m-%d')) = #{month}
   AND UC.EMAIL = #{email}

SELECT count(*) as totalCount FROM CHALLENGE_SUBMISSION CS JOIN USER_CHALLENGE UC ON CS.USER_CHALLENGE_ID = UC.USER_CHALLENGE_ID
JOIN SUBMISSION_HASH_TAG SHT ON CS.CHALLENGE_SUBMISSION_ID = SHT.CHALLENGE_SUBMISSION_ID WHERE SHT.HASH_TAG_ID IN
#{tag}
